import { Container, Row, Col } from 'react-grid';
import React, { useState, useEffect } from "react";
import { Button} from 'react-bootstrap';
import { useNavigate } from "react-router-dom";

function Profile() {
  const navigate = useNavigate();
  const [editView, setEditView] = useState(false);
  const [page, setPage] = useState();
  const [data, setData] = useState({
    email: "",
    fname: "",
    lname: "",
    number: "",
  });

  function logout(){
    fetch("/logout", {
      method: "GET",
    })
      .then(
        navigate("/")
      )
      .catch(err => {
        console.log(err);
        navigate("/");
      });

  }
  function toggleProfileEditor(){
    setEditView(!editView);
  }
  function handleSubmit(){

  }

  useEffect(() => {
    fetch("/getLoggedInUserInfo", {
      method: "GET",
      headers: {'Content-Type': 'application/json'},
    })
      .then(resp => resp.json())
      .then(data => {
        if(data.status === "success"){
          let ret_data  = {
              email: data["user_email"],
              fname: data["user_fname"],
              lname: data["user_lname"],
              number: data["user_phone"],
          };
          setData(ret_data);

          if(editView){
            console.log(data["email"]);
            setPage(
              <div className="Profile">
                <form onSubmit={handleSubmit}>
                  <div>
                    <label>
                    Email:
                    <input type="text" value={data["email"]} />
                    </label>
                  </div>
                  <div>
                    <label>
                    First Name:
                    <input type="text" value={data["fname"]} />
                    </label>
                  </div>
                  <div>
                    <label>
                    Last Name:
                    <input type="text" value={data["lname"]} />
                    </label>
                  </div>
                  <div>
                    <label>
                    Phone Number:
                    <input type="text" value={data["number"]} />
                    </label>
                  </div>
                  <input type="submit" value="Submit" />
                </form>
                <Button onClick={toggleProfileEditor}>Edit profile</Button>
              </div>

            );
          }
          else{
            setPage(
              <div className="Profile">
                <Container>
                <Row>
                  <Col>
                   Email 
                  </Col>
                  <Col>
                   {data["email"]}
                  </Col>
                </Row>
                <Row>
                  <Col>
                    First Name 
                  </Col>
                  <Col>
                   {data["fname"]} 
                  </Col>
                </Row>
                <Row>
                  <Col>
                    Last Name 
                  </Col>
                  <Col>
                   {data["lname"]} 
                  </Col>
                </Row>
                <Row>
                  <Col>
                   Phone Number 
                  </Col>
                  <Col>
                   {data["number"]} 
                  </Col>
                </Row>
              </Container>
              <Button onClick={logout}>Logout</Button>
              <Button onClick={toggleProfileEditor}>Edit profile</Button>
              </div> 
            );
          }
        }
        })
      .catch(err => console.log(err));

  }, [editView])

  return (
    <>
    {page}
    </>
  );
}

export default Profile;
